mapper.delete:
{call
declare
begin
	delete from reply where up_no=#{up_no};
	delete from upload where up_no=#{up_no};
end}

mapper.select:
SELECT up_no, comments
FROM upload
WHERE up_no = #{up_no}

mapper.update:
UPDATE upload
SET comments = #{comments}, up_date = sysdate
WHERE up_no =#{up_no}
